import * as SplashScreen from "expo-splash-screen";
import { useFonts } from "expo-font";
import { useCallback } from "react";
import "react-native-gesture-handler";
import { createStackNavigator } from "@react-navigation/stack";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import LoginForm from "./src/screens/Auth/LoginScreen";
import RegistrationForm from "./src/screens/Auth/RegistrationScreen";
import Posts from "./src/Home/Posts";
import Profile from "./src/Home/Profile";
import CreatePost from "./src/Home/CreatePost";

const AuthStack = createStackNavigator();
const MainTab = createBottomTabNavigator();

SplashScreen.preventAutoHideAsync();

export default function App() {
  const [fontsLoaded] = useFonts({
    "Roboto-Regular": require("./src/fonts/Roboto/Roboto-Regular.ttf"),
    "Roboto-Medium": require("./src/fonts/Roboto/Roboto-Medium.ttf"),
  });

  const onLayoutRootView = useCallback(async () => {
    if (fontsLoaded) {
      await SplashScreen.hideAsync();
    }
  }, [fontsLoaded]);

  if (!fontsLoaded) {
    return null;
  }

  return (
    <NavigationContainer>
      <MainTab.Navigator>
        <MainTab.Screen name="Posts" component={Posts} />
      </MainTab.Navigator>
    </NavigationContainer>
  );
}

