import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  TouchableOpacity,
  Platform,
  KeyboardAvoidingView,
  Dimensions,
} from "react-native";

const initialState = {
  email: "",
  password: "",
};

const LoginForm = () => {
  const [dataForm, setDataForm] = useState(initialState);
  const [isKeyboardVisible, setIsKeyboardVisible] = useState(false);
  const [dimensions, setDimensions] = useState(() => {
    Dimensions.get("window").width - 16 * 2;
  });

  useEffect(() => {
    const onChange = () => {
      const width = Dimensions.get("window").width - 16 * 2;
      setDimensions(width);
    };
    Dimensions.addEventListener("change", onChange);
    return () => {
      Dimensions.removeEventListener("change", onChange);
    };
  }, []);

  const handleSubmit = () => {
    setIsShowKeyboard(false);
    Keyboard.dismiss();
    setState(initialState);
  };

  return (
    <View style={styles.container}>
      <KeyboardAvoidingView
        behavior={Platform.OS === "ios" ? "padding" : "height"}
      >
        <Text style={styles.titleText}>Увійти</Text>
        <View style={{ ...styles.form, width: dimensions }}>
          <TextInput
            style={styles.input}
            placeholder="Адреса електронної пошти"
            onChangeText={(value) =>
              setDataForm((prevState) => ({ ...prevState, email: value }))
            }
            value={dataForm.login}
            keyboardType="email-address"
            onFocus={() => setIsKeyboardVisible(true)}
          />
          <TextInput
            style={styles.input}
            placeholder="Пароль"
            onChangeText={(value) =>
              setDataForm((prevState) => ({ ...prevState, password: value }))
            }
            value={dataForm.password}
            secureTextEntry={true}
            onFocus={() => setIsKeyboardVisible(true)}
          />
          <TouchableOpacity style={styles.btn}>
            <Text style={styles.textBtn}>Увійти</Text>
          </TouchableOpacity>
        </View>
        <Text style={styles.text}>Немає акаунта? Зареєструватися</Text>
      </KeyboardAvoidingView>
    </View>
  );
};

export default LoginForm;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
    borderTopStartRadius: 25,
    borderTopEndRadius: 25,
    alignItems: "center",
  },

  titleText: {
    fontFamily: "Roboto-Medium",
    fontSize: 30,
    fontWeight: 500,
    textAlign: "center",
    marginVertical: 33,
  },

  form: {},

  input: {
    height: 50,
    marginHorizontal: 16,
    marginBottom: 16,
    padding: 15,
    backgroundColor: "#F6F6F6",
    borderRadius: 8,
    color: "#BDBDBD",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },

  btn: {
    height: 50,
    marginHorizontal: 16,
    marginTop: 27,
    marginBottom: 16,
    justifyContent: "center",
    backgroundColor: "#FF6C00",
    borderRadius: 100,
  },

  textBtn: {
    textAlign: "center",
    color: "#fff",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },

  text: {
    textAlign: "center",
    marginBottom: 144,
    color: "#1B4371",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },
});
