import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  TouchableOpacity,
} from "react-native";

const LoginForm = () => {
  const [value, setValue] = useState("");
  const inputHandler = (text) => setValue(text);

  return (
    <View style={styles.container}>
      <Text style={styles.titleText}>Увійти</Text>
      <TextInput
        style={styles.input}
        placeholder="Адреса електронної пошти"
        value={value}
        onChangeText={inputHandler}
      />
      <TextInput style={styles.input} placeholder="Пароль" />
      <TouchableOpacity style={styles.btn}>Увійти</TouchableOpacity>
    </View>
  );
};

export default LoginForm;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
    borderTopStartRadius: 25,
    borderTopEndRadius: 25,
  },

  titleText: {
    fontSize: 30,
    fontWeight: 500,
    textAlign: "center",
    marginVertical: 33,
  },

  input: {
    height: 50,
    marginHorizontal: 20,
    marginBottom: 16,
    padding: 15,
    backgroundColor: "#F6F6F6",
    borderRadius: 8,
    color: "#BDBDBD",
    fontSize: 16,
    fontWeight: 400,
  },

  btn: { height: 50, textAlign: "center" },
});
