import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  TouchableOpacity,
} from "react-native";

const initialState = {
  email: "",
  password: "",
};

const LoginForm = () => {
  const [dataForm, setDataForm] = useState(initialState);
  // const [value, setValue] = useState("");
  // const inputHandler = (text) => setDataForm(text);

  const handleSubmit = () => {
    setIsShowKeyboard(false);
    Keyboard.dismiss();
    setState(initialState);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.titleText}>Увійти</Text>
      <TextInput
        style={styles.input}
        placeholder="Адреса електронної пошти"
        value={dataForm.login}
        onChangeText={(value) =>
          setDataForm((prevState) => ({ ...prevState, email: value }))
        }
        keyboardType="email-address"
      />
      <TextInput
        style={styles.input}
        placeholder="Пароль"
        value={dataForm.password}
        onChangeText={(value) =>
          setDataForm((prevState) => ({ ...prevState, password: value }))
        }
        secureTextEntry={true}
        onFocus={() => setIsShowKeyboard(true)}
      />
      <TouchableOpacity style={styles.btn}>
        <Text style={styles.textBtn}>Увійти</Text>
      </TouchableOpacity>
      <Text style={styles.text}>Немає акаунта? Зареєструватися</Text>
    </View>
  );
};

export default LoginForm;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
    borderTopStartRadius: 25,
    borderTopEndRadius: 25,
  },

  titleText: {
    fontFamily: "Roboto-Medium",
    fontSize: 30,
    fontWeight: 500,
    textAlign: "center",
    marginVertical: 33,
  },

  input: {
    height: 50,
    marginHorizontal: 16,
    marginBottom: 16,
    padding: 15,
    backgroundColor: "#F6F6F6",
    borderRadius: 8,
    color: "#BDBDBD",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },

  btn: {
    height: 50,
    marginHorizontal: 16,
    marginTop: 27,
    marginBottom: 16,
    justifyContent: "center",
    backgroundColor: "#FF6C00",
    borderRadius: 100,
  },

  textBtn: {
    textAlign: "center",
    color: "#fff",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },

  text: {
    textAlign: "center",
    marginBottom: 144,
    color: "#1B4371",
    fontFamily: "Roboto-Regular",
    fontSize: 16,
    fontWeight: 400,
  },
});
