import React from "react";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { getFocusedRouteNameFromRoute } from "@react-navigation/native";

import LoginForm from "../screens/Auth/LoginScreen";
import RegistrationForm from "../screens/Auth/RegistrationScreen";
import Posts from "../screens/Home/Posts";
import Profile from "../screens/Home/Profile";
import CreatePost from "../screens/Home/CreatePost";

const AuthStack = createStackNavigator();
const MainTab = createBottomTabNavigator();

const useRoute = (isAuth) => {
  if (!isAuth) {
    return (
      <AuthStack.Navigator>
        <AuthStack.Screen
          options={{ headerShown: false }}
          name="Login"
          component={LoginForm}
        />
        <AuthStack.Screen
          options={{ headerShown: false }}
          name="Register"
          component={RegistrationForm}
        />
      </AuthStack.Navigator>
    );
  }
  return (
    <MainTab.Navigator
      initialRouteName="Posts"
      screenOptions={{
        headerTitleAlign: "center",
        headerTitleStyle: {
          fontFamily: "Roboto-Medium",
          fontSize: 17,
          lineHeight: 22,
        },
        headerStyle: {
          backgroundColor: "#ffffff",
          borderBottomWidth: 0.5,
          borderBottomColor: "#21212120",
        },
        tabBarOptions: {
          tabBarHideOnKeyboard: true,
        },
      }}
      tabBarOptions={{ showLabel: false }}
    >
      <MainTab.Screen name="Posts" component={Posts} />
      <MainTab.Screen name="CreatePost" component={CreatePost} />
      <MainTab.Screen name="Profile" component={Profile} />
    </MainTab.Navigator>
  );
};

export default useRoute;
